Webtalk.helpers.folderList = function(element){

  $(element).metisMenu({
  toggle: true, // auto collapse.
  doubleTapToGo: false, // double tap to go
  activeClass: 'active' // name 'active' CSS class
  });

  //Manage Tree lines gray and blue
  var style = $("<style>");
  $("html > head").append(style);
  showLineHor(element+'>li');
	
  $(element).each(function(){

    $(this).find("li").each(function(i){
      
      if ( typeof $(this).attr("class") != 'undefined' && $(this).attr("class").search("line-blue") != -1 ){
        var line = $("<div>").addClass("__line-blue__");
        $(this).parent().parent().append( line );
      }

      calcHeightLineBlue(line, $(this));

      //Event click
      $(this).on("click", function(e){
        e.stopPropagation();
        var el = $(this);
		var ul = el.parent();
        showLineHor(el);

        setTimeout(function(){ //Sleep while open all tree

			var random = Math.floor((Math.random() * 1000000) + 1);
			var _class = "class-li-height-"+random;
			var height = parseFloat ( $(ul).find("> li:last-child").css("height") ) - 39;
			var css = "."+_class+":before{ height: calc( 100% - "+ height +"px) !important; }";
			style.append(css);
			ul.removeClassWild("class-li-height-*");
			ul.addClass(_class);

            $(element).each(function(){ 
              $(this).find("li > .__line-blue__").each(function(i){
                calcHeightLineBlue($(this), $(this).parent().find("ul > li.line-blue") );
              });
            });
			  
        }, 350); 
       
      });
		
    });
  });
	
  function showLineHor(li){
    $(li).find(">ul>li").removeClass("left--line");
    setTimeout(function(){
      $(li).find(">ul>li").addClass("left--line");
    },350);
  }

  function calcHeightLineBlue(line_vert, line_hor){ 
    if( typeof line_vert != 'undefined' && typeof line_hor != 'undefined'){
      var mid = 17;
      var h = Math.abs(line_vert.offset().top - line_hor.offset().top);
      line_vert.css("height", (h+mid) +"px");
    }
  }

}

