Webtalk.helpers.dynamicText = function(inputProfessionally, inputPersonally, dynamicPhrase, dynamicPhraseOne, dynamicPhraseTwo, dynamicPhraseAnd){

  $(dynamicPhrase).hide();
  var dynamicAnd = 0;
  var dynamicAndFunction = function(){
    if( dynamicAnd == 2 ){
        $(dynamicPhraseAnd).show(); 
    }else{
     $(dynamicPhraseAnd).hide(); 
    }
  };

  $(inputProfessionally).on('click', function () {
    if ($(this).is(':checked')){
      $(dynamicPhraseOne).show();
      dynamicAnd += 1;
    }
    else{
      $(dynamicPhraseOne).hide();
      if (dynamicAnd >= 1 && dynamicAnd <= 2) {
        dynamicAnd -= 1;
      };
    }
    dynamicAndFunction();

  });

  $(inputPersonally).on('click', function () {
    if ($(this).is(':checked')){
      $(dynamicPhraseTwo).show();
      dynamicAnd += 1;
    }
    else{
      $(dynamicPhraseTwo).hide(); 
      dynamicAnd -= 1;
    }
    dynamicAndFunction();
  });

}

Webtalk.helpers.checkBehavior = function(checkKey, checkItem){

  $(document).ready(function () {
    $(checkKey).click(function () {
      $(checkItem).prop('checked', this.checked);
      
      if(this.checked)
        $(this).parents(".mobile-post-group").addClass("check");
      else
        $(this).parents(".mobile-post-group").removeClass("check");
      
    });
    $(checkItem).click(function () {
      var itemsChecked = $(checkItem+':checked').length;
      if(itemsChecked == $(checkItem).length){
      $(checkKey).prop('checked', this.checked);
      }
      if(itemsChecked == 0){
      $(checkKey).prop('checked', false);
      }

      if(itemsChecked > 0){
      $(checkKey).prop('checked', true);
      }
    });
  });

}

